.channel-menu-mini{padding:10px 5px;background:#fff;box-shadow:0 0 5px rgba(0,0,0,.15);width:670px;height:420px;display:-ms-flexbox;display:flex;text-align:left}.channel-menu-mini .box{display:-ms-flexbox;display:flex;-ms-flex-direction:column;flex-direction:column;-ms-flex-wrap:wrap;flex-wrap:wrap}.channel-menu-mini .box a{display:-ms-flexbox;display:flex;width:172px;height:24px;line-height:24px;padding:10px 20px!important;color:#212121;border-radius:4px;transition:all .3s;font-size:14px;-ms-flex-pack:justify;justify-content:space-between;box-sizing:content-box}.channel-menu-mini .box a:hover{color:#212121;background:#f4f4f4}.channel-menu-mini .box .name svg{margin-right:10px}.channel-menu-mini .box .count{color:#999}.channel-menu-mini .l-box{-ms-flex:2;flex:2}.channel-menu-mini .r-box{-ms-flex:1;flex:1;padding-left:5px;border-left:1px solid #e7e7e7}.channel-menu-mini .svg-icon{width:1.8em;height:1.8em;vertical-align:bottom;fill:currentColor;overflow:hidden}