.lang-change{width:100%;border-bottom:1px solid #f4f4f4;padding:7px 0;position:relative;font-size:14px}.lang-change .lang-intro{padding:8px 0;top:0;visibility:hidden;opacity:0;position:absolute;left:279px;background:#fff;width:240px;font-size:12px;color:#212121;box-shadow:0 3px 6px 0 rgba(0,0,0,.2);transition:.3s ease;border-radius:2px}.lang-change .lang-intro-item{font-size:14px;padding:8px 15px;line-height:22px;display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between;cursor:pointer;transition:all .3s}.lang-change .lang-intro-item .bilifont{font-size:22px;display:none}.lang-change .lang-intro-item:hover{background:#f4f4f4}.lang-change .lang-intro-item.on{color:#00a1d6}.lang-change .lang-intro-item.on .bilifont{display:block}.lang-change:hover .lang-intro{visibility:visible;opacity:1}.lang-change .lang-item{cursor:pointer;display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between;-ms-flex-align:center;align-items:center;transition:.3s ease;padding:8px 23px}.lang-change .lang-item:hover{background:#f4f4f4}.lang-change .lang-title{font-size:14px;color:#212121;display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center}.lang-change .lang-icon{font-size:24px;vertical-align:middle;color:#979797;margin-right:5px}.lang-change .lang-icon-more{font-size:16px;vertical-align:middle;color:#979797}