.loc-menu-box{background:#fff;width:399px;padding:12px;border-bottom-left-radius:4px;border-bottom-right-radius:4px;box-shadow:0 1px 2px rgba(0,0,0,.1)}.loc-menu-box .loc-link{display:block;position:relative}.loc-menu-box .loc-link img{width:375px;height:110px;border-radius:4px}.loc-menu-box .loc-link span{position:absolute;color:#fff;left:0;bottom:0;width:375px;line-height:24px;padding-left:10px;border-radius:4px;text-align:left;box-sizing:border-box;background:linear-gradient(transparent,rgba(0,0,0,.6))}.loc-menu-box .loc-link:first-child{margin-bottom:12px}.loc-moveclip{height:36px}.loc-mc-box,.loc-moveclip{width:54px;overflow:hidden}.loc-mc-box{height:108px;position:relative;animation:locmoveclipslider 10s ease-out infinite;animation-delay:4.5s}.loc-mc-box .bnj-gif{position:absolute;left:0;top:36px;width:54px;height:36px;transform:translateZ(0)}.loc-mc-box .bnj-txt{line-height:36px;text-align:center;width:100%}.loc-mc-box .txt-up{position:absolute;left:0;top:0}.loc-mc-box .txt-down{position:absolute;left:0;top:72px}@keyframes locmoveclipslider{0%{transform:translateY(0)}5%{transform:translateY(-36px)}50%{transform:translateY(-36px)}55%{transform:translateY(-72px)}to{transform:translateY(-72px)}}