.nav-user-center{display:-ms-flexbox;display:flex;-ms-flex-negative:0;flex-shrink:0}.nav-user-center .user-con{display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center}.nav-user-center .user-con.search-icon{display:none}.nav-user-center .user-con.search-icon .bilifont{font-size:20px}.nav-user-center .user-con .item{position:relative;display:-ms-flexbox;display:flex;margin-left:12px;cursor:pointer}.nav-user-center .user-con .item .txt{color:#fff;white-space:nowrap;font-size:14px;line-height:32px}.nav-user-center .user-con .item .name{color:#fff;text-shadow:0 1px 1px rgba(0,0,0,.3);white-space:nowrap}.nav-user-center .user-con .item .tips{position:absolute;top:38px;left:-81px;padding:2px 6px 2px 2px;border-radius:4px;background:#00a1d6;box-shadow:0 3px 6px 0 rgba(0,0,0,.2);color:#fff;white-space:nowrap;z-index:1}.nav-user-center .user-con .item .tips:before{position:absolute;top:-2px;left:50%;margin-left:-4px;width:8px;height:8px;background:#00a1d6;content:"";transform:rotate(45deg)}.nav-user-center .user-con .fall-mini-avatar{width:36px;height:36px;position:relative;margin-right:10px}.nav-user-center .user-con .fall-mini-avatar.unlogin{margin-right:16px}.nav-user-center .user-con .fall-mini-avatar img{width:36px;height:36px;border-radius:50%}.nav-user-center .user-con .fall-mini-avatar .unlogin-mark{position:absolute;top:-13px;left:-3px;display:inline-block;width:80px;height:32px;border-radius:16px;line-height:28px;font-size:20px;background:#fb7299;color:#fff;text-align:center;border:2px solid #fff;transform:scale(.5)}.mini-type .nav-user-center .user-con .item .name{color:#212121;text-shadow:none}