.van-popper-channel{padding:5px 10px;border-radius:3px;min-width:0}.van-popper-channel .sub-container,.van-popper-channel .sub-item{display:-ms-flexbox;display:flex}.van-popper-channel .sub-item{-ms-flex-direction:column;flex-direction:column;-ms-flex-wrap:wrap;flex-wrap:wrap;width:100%;max-height:148px}.van-popper-channel .sub-item .name{display:block;font-size:12px;padding:10px 13px;height:37px}.van-popper-channel .popper__arrow{margin-left:-45px}.channel-menu-itnl{display:-ms-flexbox;display:flex;-ms-flex-direction:column;flex-direction:column;-ms-flex-wrap:wrap;flex-wrap:wrap;height:5rem;height:68px;-ms-flex:1;flex:1}.channel-menu-itnl .item{cursor:pointer;display:-ms-flexbox;display:flex;height:34px}.channel-menu-itnl .item .name{font-size:14px;display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;white-space:nowrap}.channel-menu-itnl .item .name em{font-style:normal;font-size:12px;display:inline-block;background:#73c9e5;border-radius:2px;color:#fff;margin-left:1px;transform:scale(.85);width:32px;text-align:center}.channel-menu-itnl .item .name.on{color:#00a1d6}.channel-menu-itnl .item .bili-icon_caozuo_xiangyou-copy{margin-left:5px;transition:all .3s}.channel-menu-itnl .item.selected .name{color:#00a1d6}.channel-menu-itnl .item.selected .bili-icon_caozuo_xiangyou-copy{transform:rotate(180deg)}