.page-tab{-ms-flex-align:center;align-items:center}.page-tab,.page-tab .con{display:-ms-flexbox;display:flex}.page-tab .con li{cursor:pointer;width:66px;height:68px;text-align:center;font-size:14px;white-space:nowrap;border:1px solid #fff;border-radius:16px;transition:all .3s}.page-tab .con li:hover a{color:#00a1d6}.page-tab .con li.on{border-color:#9dd9ed;background:#f1fcff}.page-tab .con li.on a{color:#00a1d6}.page-tab .con li .round{position:relative;width:36px;height:36px;background:#ff5c7c;border-radius:36px;line-height:36px;text-align:center;margin:0 auto 4px}.page-tab .con li .round.yel{background:#fcba2a}.page-tab .con li .round.blue{background:#00a1d6}.page-tab .con li .round.orange{background:#ff716d}.page-tab .con li .round .dynamic-update,.page-tab .con li .round .dynamic-update img{position:absolute;width:36px;height:36px;left:0;top:0}.page-tab .con li .round .dynamic-update img{border-radius:50%}.page-tab .con li .round .dynamic-update i{position:absolute;right:-2px;top:-2px;width:10px;height:10px;border-radius:50%;background:#fa5a57;border:2px solid #fff}.page-tab .con li .round.channel{background:#6dc781}.page-tab .con li .bilifont{color:#fff;font-size:28px}.page-tab .con .channel-name{width:64px;height:20px;display:block;overflow:hidden;-webkit-box-orient:vertical;text-overflow:-o-ellipsis-lastline;text-overflow:ellipsis;word-break:break-all;-webkit-line-clamp:1}