.bilibili-search-history{position:absolute;width:100%;border:1px solid #e5e9ef;margin-top:1px;background:#fff;z-index:99999;border-radius:2px;box-shadow:0 2px 4px rgba(0,0,0,.16);padding:10px 0;font-size:14px}.bilibili-search-history .history-item{padding:6px 10px 6px 16px;height:32px;line-height:32px;cursor:pointer;word-wrap:break-word;word-break:break-all;color:#222;position:relative;display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;-ms-flex-pack:justify;justify-content:space-between;transition:.2s ease}.bilibili-search-history .history-item.focus,.bilibili-search-history .history-item:hover{background-color:#f4f4f4}.bilibili-search-history .history-item a{width:100%;color:#222;display:block;overflow:hidden;text-overflow:ellipsis;white-space:nowrap}.bilibili-search-history .history-item a:hover{color:#222}.bilibili-search-history .history-item a.link-wrp{display:block}.bilibili-search-history .cancel-icon{color:#999}.bilibili-search-history .cancel-icon:hover{color:#00a1d6}