.bili-header-m .nav-search-box,.international-header .nav-search-box{margin:0 10px;width:500px;transition:width .3s}.bili-header-m .nav-search,.international-header .nav-search{position:relative}.bili-header-m .nav-search #nav_searchform,.international-header .nav-search #nav_searchform{display:block;padding:0 38px 0 16px;border:1px solid hsla(0,0%,100%,0);border-radius:2px;background-color:#fff}.bili-header-m .nav-search .nav-search-keyword,.international-header .nav-search .nav-search-keyword{overflow:hidden;width:100%;height:34px;border:none;background-color:transparent;box-shadow:none;color:#999;font-size:14px;line-height:34px;transition:all .2s}.bili-header-m .nav-search .nav-search-keyword::-ms-clear,.international-header .nav-search .nav-search-keyword::-ms-clear{display:none}.bili-header-m .nav-search .nav-search-keyword:focus,.international-header .nav-search .nav-search-keyword:focus{color:#222}.bili-header-m .nav-search .nav-search-submit,.international-header .nav-search .nav-search-submit{position:absolute;top:8px;right:16px;margin:0;padding:0;border:none;background:none;color:#505050;font-size:16px;line-height:20px;cursor:pointer;transition:all .2s}.bili-header-m .nav-search .nav-search-btn,.international-header .nav-search .nav-search-btn{position:absolute;top:0;right:0;margin:0;padding:0;width:48px;height:36px;border:none;border-radius:2px;background:#e7e7e7;line-height:26px;cursor:pointer}.bili-header-m .nav-search .nav-search-btn:hover .nav-search-submit,.international-header .nav-search .nav-search-btn:hover .nav-search-submit{color:#00a1d6}.bili-header-m .bilibili-suggest,.international-header .bilibili-suggest{position:relative;z-index:10000;margin-top:2px;padding-bottom:5px;border:1px solid #e5e9ef;border-radius:4px;background:#fff;box-shadow:0 2px 4px rgba(0,0,0,.16);font-size:12px}