.bilibili-search-suggest{position:absolute;width:100%;border:1px solid #e5e9ef;margin-top:1px;background:#fff;z-index:99999;border-radius:2px;box-shadow:0 2px 4px rgba(0,0,0,.16);padding:10px 0;font-size:14px}.bilibili-search-suggest .suggest-item{padding:0 16px;height:32px;line-height:32px;cursor:pointer;word-wrap:break-word;word-break:break-all;display:block;color:#222;position:relative;transition:.2 ease}.bilibili-search-suggest .suggest-item.focus,.bilibili-search-suggest .suggest-item:hover{background-color:#f4f4f4}.bilibili-search-suggest .suggest-item a{width:100%;overflow:hidden;text-overflow:ellipsis;white-space:nowrap;color:#222;display:block}.bilibili-search-suggest .suggest-item a.link-wrp{display:block}.bilibili-search-suggest .suggest-item a:hover{color:#222}.bilibili-search-suggest .suggest-item .suggest_high_light{font-style:normal;color:#f25d8e}