.try-feedback{position:fixed;z-index:101;left:0;top:50%;margin-top:-36px;width:28px;height:72px;border-top-right-radius:2px;border-bottom-right-radius:2px;background-color:#fb7299;transition:all .3s;font-size:12px;color:#fff;box-shadow:0 6px 10px 0 rgba(251,114,153,.4);padding:8px 7px;line-height:14px}.try-feedback:hover{background-color:#ff85ad;color:#fff}.contact-help{position:fixed;z-index:101;left:0;top:calc(50% - 30px);margin-top:-36px;width:28px;height:72px;transition:all .3s;font-size:12px;color:#505050;background:#fff;border:1px solid #e7e7e7;box-shadow:0 6px 10px 0 #e7e7e7;border-radius:0 2px 2px 0;padding:8px 7px;line-height:14px}.contact-help:hover{background:#f4f4f4;color:#505050}