.focus-carousel{position:relative;width:550px;height:242px;overflow:hidden;border-radius:2px}.focus-carousel .ggc{width:100%!important;height:100%!important;z-index:1}.focus-carousel img{width:100%;height:100%}.focus-carousel .more{position:absolute;z-index:11;opacity:0;transition:opacity .3s;bottom:44px;right:12px;font-size:12px;padding:4px 8px;background:rgba(0,0,0,.65);color:#fff;border-radius:2px}.focus-carousel .more i{vertical-align:middle}.focus-carousel:hover .more{opacity:1}.focus-carousel .bypb-icon{display:inline-block;vertical-align:middle;width:38px;height:22px;background-image:url(data:image/png;base64,);background-size:cover;border-radius:2px;margin-right:3px}