.elevator{position:fixed;left:calc(50% + 3px);top:10px;margin-left:825px;z-index:10}.elevator .list-box{padding-top:6px;position:relative;background:#fff;border:1px solid #e7e7e7;border-top-left-radius:10px;border-top-right-radius:10px;z-index:2}.elevator .list-box .item{width:54px;height:24px;line-height:24px;text-align:center;cursor:pointer;transition:all .2s;-ms-user-select:none;user-select:none;background:#fff}.elevator .list-box .item.sort{height:32px;line-height:32px;position:relative;border-top:1px solid #e7e7e7;margin-top:5px}.elevator .list-box .item.back-top{position:absolute;left:-1px;bottom:-32px;width:56px;line-height:30px;height:32px;border:1px solid #e7e7e7;border-bottom-left-radius:10px;border-bottom-right-radius:10px}.elevator .list-box .item.back-top .bilifont{font-size:26px}.elevator .list-box .item.back-old{position:absolute;width:56px;height:42px;line-height:16px;left:-1px;bottom:-82px;border-radius:10px;border:1px solid #e7e7e7;display:-ms-flexbox;display:flex;-ms-flex-pack:center;justify-content:center;-ms-flex-align:center;align-items:center}.elevator .list-box .item .bilifont{color:#999}.elevator .list-box .item.on,.elevator .list-box .item:hover{background-color:#00a1d6;color:#fff}.elevator .list-box .item.on .bilifont,.elevator .list-box .item:hover .bilifont{color:#fff}.elevator .ear{position:absolute;top:-10px;font-size:12px;left:3px;z-index:2}.elevator .bg23{display:none;position:absolute;width:130px;height:105%;top:-20px;right:-15px;background-color:hsla(0,0%,100%,.8);padding:40px;box-sizing:content-box;border-radius:2px;background-image:url(https://s1.hdslb.com/bfs/static/jinkela/home/asserts/tab2233.png);background-repeat:no-repeat;background-position:16px 20px}.elevator.edit{z-index:1000}.elevator.edit .mask{position:fixed;left:0;top:0;right:0;bottom:0;width:100%;height:100%;background-color:rgba(0,0,0,.5)}.elevator.edit .bg23{display:block}.slicksort-selected{text-align:center;line-height:28px;background-color:#00a1d6;color:#fff;pointer-events:stroke!important;z-index:1001}.slicksort-selected .bilifont{color:#fff}