.rank-wrap{display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between;margin-bottom:18px}.rank-wrap:last-child{margin-bottom:0}.rank-wrap .number{font-size:14px;color:#999;width:18px;height:18px;text-align:center;line-height:18px;background:#fff;border-radius:2px;display:inline-block}.rank-wrap .number.on{color:#fff;background:#00a1d6}.rank-wrap .link{display:inline-block}.rank-wrap .preview{position:relative;display:-ms-flexbox;display:flex;width:290px}.rank-wrap .preview .pic{position:relative}.rank-wrap .preview .pic .watch-later-video{transition:opacity .3s;opacity:0}.rank-wrap .preview .pic:hover .watch-later-video{transition-delay:.2s;opacity:1}.rank-wrap .preview img{width:112px;height:63px;border-radius:2px}.rank-wrap .preview .txt{margin-left:12px}.rank-wrap .preview .txt p{font-size:14px;height:40px;line-height:20px;overflow:hidden;text-overflow:ellipsis;display:-webkit-box;-webkit-line-clamp:2;
  /*! autoprefixer: ignore next */-webkit-box-orient:vertical;margin-bottom:5px;word-break:break-word!important;word-break:break-all}.rank-wrap .preview .txt span{color:#999}.rank-wrap .title{width:290px;font-size:14px;line-height:20px;overflow:hidden;white-space:nowrap;text-overflow:ellipsis;word-break:break-word!important;word-break:break-all}