.manga-panel .left-slot,.manga-panel .tab-switch{display:-ms-flexbox;display:flex}.manga-panel .tab-switch{margin-left:4px;margin-top:1px}.manga-panel .tab-switch .tab-switch-item{margin-right:12px;height:30px;font-size:12px;line-height:30px;cursor:pointer}.manga-panel .tab-switch .tab-switch-item.on{border-bottom:1px solid #00a1d6;color:#00a1d6}.manga-panel .app-download-link{position:relative;height:30px;color:#505050;text-align:center;font-size:12px;line-height:32px;cursor:pointer}.manga-panel .app-download-link:hover{color:#00a1d6}.manga-panel .app-download-link span{position:absolute;top:-10px;right:-54px;width:72px;height:20px;border-radius:10px;background:#fa5a57;color:#fff;font-size:12px;line-height:19px;text-align:center}.manga-panel .manga-list-box{display:-ms-flexbox;display:flex;-ms-flex-line-pack:justify;align-content:space-between;-ms-flex-wrap:wrap;flex-wrap:wrap;-ms-flex-pack:justify;justify-content:space-between;width:1286px;height:682px}.manga-panel .manga-list-box .manga-card{width:206px;margin-bottom:24px}.manga-panel .manga-list-box .manga-card>img{display:block;width:206px;height:275px;background-repeat:no-repeat;background-position:50%;border-radius:2px;background-image:url(data:image/png;base64,)}.manga-panel .manga-list-box .manga-card .manga-title{max-width:206px;margin:10px 0 8px;color:#212121;font-weight:500;font-size:14px;transition:.3s;overflow:hidden;text-overflow:ellipsis;white-space:nowrap;text-align:left}.manga-panel .manga-list-box .manga-card .manga-tag{color:#999;font-size:12px;overflow:hidden;line-height:16px;text-overflow:ellipsis;white-space:nowrap}.manga-panel .manga-list-box .manga-card:hover .manga-title{color:#00a1d6}