.manga-rank{overflow:hidden;width:320px}.manga-rank .tab-switch{display:-ms-flexbox;display:flex;-ms-flex:1;flex:1;margin-left:20px;margin-top:2px}.manga-rank .tab-switch .tab-switch-item{margin-right:12px;height:21px;font-size:12px;line-height:16px;cursor:pointer}.manga-rank .tab-switch .tab-switch-item.on{border-bottom:1px solid #00a1d6;color:#00a1d6}.manga-rank .manga-rank-wrap{display:-ms-flexbox;display:flex;width:300%;transition:all .2s ease-in-out;zoom:1}.manga-rank .manga-rank-wrap .manga-list{-ms-flex-negative:0;flex-shrink:0}.manga-rank .manga-rank-wrap.show-fans{margin-left:-100%}.manga-rank .manga-rank-wrap.show-free{margin-left:-200%}