.exchange-btn,.exchange-btn .btn{display:-ms-flexbox;display:flex}.exchange-btn .btn{-ms-flex-align:center;align-items:center;cursor:pointer;height:22px;text-align:center;border:1px solid silver;border-radius:2px;font-size:12px;color:#505050;transition:all .2s;line-height:16px}.exchange-btn .btn .bilifont{vertical-align:middle}.exchange-btn .btn:hover{background-color:#f4f4f4}.exchange-btn .btn:active{background-color:#ccc}.exchange-btn .more{padding:0 0 0 12px;width:58px;margin-left:12px}.exchange-btn .btn-change{padding:0 0 0 6px;width:72px}.exchange-btn .btn-change .bilifont{margin-right:4px;transition:all .5s}.exchange-btn .btn-change .bilifont.quan{animation:quan .8s linear infinite}.exchange-btn .btn-change:hover .bilifont{transform:rotate(-1turn)}@keyframes quan{0%{transform:rotate(1turn)}25%{transform:rotate(270deg)}50%{transform:rotate(180deg)}75%{transform:rotate(90deg)}to{transform:rotate(0deg)}}