.time-line .zone-list-box{width:1287px;height:376px;display:-ms-flexbox;display:flex;-ms-flex-wrap:wrap;flex-wrap:wrap;-ms-flex-line-pack:start;align-content:flex-start;-ms-flex-pack:start;justify-content:flex-start;overflow:auto}.time-line .zone-list-box .item{margin:0 30px 24px 0}.time-line .tab-switch{display:-ms-flexbox;display:flex;margin-left:4px}.time-line .tab-switch .tab-switch-item{font-size:14px;line-height:30px;height:30px;margin-right:24px;cursor:pointer}.time-line .tab-switch .tab-switch-item.on{border-bottom:1px solid #00a1d6;color:#00a1d6}.time-line .tl-link{width:112px;height:30px;border:1px solid #00a1d6;border-radius:2px;background:#fff;text-align:center;line-height:27px;padding:0 0 0 14px;font-size:14px;color:#00a1d6;display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;transition:all .2s;line-height:16px}.time-line .tl-link:hover{color:#fff;background:#00a1d6}