.time-line-card{width:227px;height:70px;display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between}.time-line-card .pic{margin-right:12px}.time-line-card .pic img{width:70px;height:70px;border-radius:2px}.time-line-card .txt{width:152px;font-weight:500}.time-line-card .txt .ss{display:inline-block;font-size:14px;height:37px;overflow:hidden;text-overflow:ellipsis;display:-webkit-box;-webkit-line-clamp:2;
  /*! autoprefixer: ignore next */-webkit-box-orient:vertical;margin:2px 0 12px}.time-line-card .txt .update a{width:100%;color:#999;display:inline-block;overflow:hidden;white-space:nowrap;text-overflow:ellipsis;font-weight:400}.time-line-card .txt .update .published{color:#00a1d6}