.pgc-rank-wrap{display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between;margin-bottom:18px}.pgc-rank-wrap:last-child{margin-bottom:0}.pgc-rank-wrap .number{font-size:14px;color:#999;width:18px;height:18px;text-align:center;line-height:18px;background:#fff;border-radius:2px;display:inline-block}.pgc-rank-wrap .number.on{color:#fff;background:#00a1d6}.pgc-rank-wrap .link{display:inline-block}.pgc-rank-wrap .txt{width:290px;line-height:20px;display:-ms-flexbox;display:flex;-ms-flex-pack:justify;justify-content:space-between}.pgc-rank-wrap .txt .title{width:198px;font-size:14px;font-weight:500}.pgc-rank-wrap .txt .title,.pgc-rank-wrap .txt .update{overflow:hidden;white-space:nowrap;text-overflow:ellipsis}.pgc-rank-wrap .txt .update{font-size:12px;color:#999;text-align:right;min-width:90px}.pgc-rank-wrap .txt .movie-update{min-width:100px}