.rank-header{display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;-ms-flex-pack:justify;justify-content:space-between;margin-bottom:16px;height:36px}.rank-header .name{display:inline-block;color:#212121;vertical-align:bottom;font-size:20px;line-height:28px}.rank-header .more{display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;padding:0 0 0 12px;width:58px;height:22px;border:1px solid silver;border-radius:2px;color:#505050;text-align:center;font-size:12px;line-height:16px;transition:all .2s}.rank-header .more .bilifont{vertical-align:middle}.rank-header .more:hover{background-color:#f4f4f4}