.storey-title{display:-ms-flexbox;display:flex;-ms-flex-align:center;align-items:center;-ms-flex-pack:justify;justify-content:space-between;margin-bottom:16px;height:36px}.storey-title .l-con{display:-ms-inline-flexbox;display:inline-flex}.storey-title .l-con .svg-icon{margin-right:6px}.storey-title .l-con .sprite{width:36px;height:36px;margin-right:6px}.storey-title .name{margin:0 20px 0 0;vertical-align:bottom;font-size:20px;line-height:36px}.storey-title .no-link{color:#212121;cursor:default}.storey-title .text-info{color:#505050;line-height:36px}.storey-title .text-info a{color:#505050}.storey-title .text-info a:hover{color:#00a1d6}.storey-title .text-info a .bilifont{margin-right:3px;color:#f0361b}