.video-card-reco{position:relative;width:206px;height:116px}.video-card-reco .info-box{position:relative;overflow:hidden;background-image:url(data:image/png;base64,);background-repeat:no-repeat;background-position:50%}.video-card-reco .info-box,.video-card-reco .info-box:before,.video-card-reco .info-box img{width:100%;height:100%;border-radius:2px}.video-card-reco .info-box:before{content:"";position:absolute;left:0;top:0;background:rgba(0,0,0,.7);opacity:0;transition:opacity .2s}.video-card-reco .info-box .info{position:absolute;z-index:2;width:100%;top:64px;left:0;transition:top .2s;padding:26px 10px 10px}.video-card-reco .info-box .info .title{font-size:14px;line-height:18px;height:18px;color:#fff;margin-bottom:6px;overflow:hidden;text-overflow:ellipsis;white-space:nowrap;font-weight:500}.video-card-reco .info-box .info .play,.video-card-reco .info-box .info .up{font-size:12px;color:#e0e0e0;margin-bottom:3px}.video-card-reco .info-box .info .play .bilifont,.video-card-reco .info-box .info .up .bilifont{vertical-align:middle;margin-right:5px}.video-card-reco .watch-later-video{transition:opacity .2s;opacity:0}.video-card-reco:before{content:"";position:absolute;z-index:1;bottom:0;left:0;width:100%;height:48px;background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAwCAYAAAGnNCAXAAAABGdBTUEAALGPC/xhBQAAAENJREFUCB1jYGBgYGICEpIgQgqNBRRi4MMmARYDyXKAWLwgggfOAnMJiIH0soJ0sMEJdlQWWBYshpAAK0ZwwSzS1AEAes8Ckyqvlc0AAAAASUVORK5CYII=);background-repeat:repeat-x;border-radius:0 0 2px 2px}.video-card-reco:hover .watch-later-video{transition-delay:.2s;opacity:1}.video-card-reco:hover .info-box:before{opacity:1}.video-card-reco:hover .info-box .info{top:0}.video-card-reco:hover .info-box .info .title{height:36px;white-space:normal}